package second.logic;

public class NumberTriangle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     for(int row=1; row<=7;row++)
     {
    	 
    	 for (int column=1; column<=row;column++)
    	 {
    		 System.out.print(row+"");
    		 
    	 }
    	 System.out.println("");
    	 }
     
     }
	}

